mapper.select:
		SELECT *
		 FROM store


				category_no = #{ categoryNo }	


				category_no = #{ categoryNo }	


				AND store_address LIKE CONCAT('%',#{type},'%') AND store_name LIKE CONCAT('%',#{keyword},'%')




                         store_name ASC 
                         store_review_count DESC 
                         store_bookmarks DESC 
                        store_no DESC


		LIMIT #{startRow}, #{num}

SELECT COUNT(store_no)
 FROM store


		category_no = #{ categoryNo }	


		category_no = #{ categoryNo }	


		AND store_address = #{type} AND store_name LIKE CONCAT('%',#{keyword},'%')

mapper.update:
UPDATE store
	SET store_bookmarks = store_bookmarks + "1"
WHERE store_no = #{storeNo}

UPDATE store
	SET store_bookmarks = IF(store_bookmarks < "1", "0", store_bookmarks - "1")
WHERE store_no = #{storeNo}
