mapper.select:
SELECT * FROM post WHERE store_no = #{storeNo} AND post_is_post = 1
post_reg_date ASC post_up_count DESC post_no DESC
LIMIT #{startRow}, #{num}

SELECT count(post_no) FROM post 
WHERE store_no = #{storeNo}
AND post_is_post = 0

SELECT count(post_no) FROM post 
WHERE store_no = #{storeNo}
AND post_is_post = 0

SELECT * FROM post
	WHERE post_no = #{ postNo } 
ORDER BY post_no DESC

mapper.update:
UPDATE post 
	SET post_title = #{postTitle}, post_content = #{postContent},
		post_file1 = #{postFile1}, post_file2 = #{postFile2},
		post_file3 = #{postFile3}, post_file4 = #{postFile4}, post_file5 = #{postFile5}
WHERE post_no = #{postNo}
